mapper.select:
SELECT idx, module_id, module_name, module_metadata, course_name, course_metadata, course_create_id, userid as course_create_userid, user_division, create_date, modify_date FROM public.courses_infos
module_name like #{keyword} or course_name like #{keyword} or userid like #{keyword}
OFFSET ${offset} LIMIT ${limit}

SELECT courses_infos.idx, courses_infos.module_id, courses_infos.module_name, courses_infos.module_metadata, courses_infos.course_name, courses_infos.course_metadata, courses_infos.course_create_id, courses_infos.userid as course_create_userid, courses_infos.user_division, courses_infos.create_date, courses_infos.modify_date, courses_member.user_id, courses_member.join_status as status FROM courses_infos courses_infos left join courses_member courses_member ON courses_infos.idx = courses_member.course_id WHERE courses_infos.user_id = ${userId}
and ( module_name like #{keyword} or course_name like #{keyword} or userid like #{keyword} )
OFFSET ${offset} LIMIT ${limit}

mapper.update:
UPDATE courses
SET
	course_name = #{courseName}, 
	course_metadata = #{courseMetadata}, 
	modify_date = #{modifyDate} 
WHERE idx = #{idx}
