mapper.update:
UPDATE courses_team
SET 
	team_name = #{teamName},
	modify_date = #{modifyDate}
WHERE course_id = #{courseId} AND idx = #{teamId}

mapper.select:
SELECT
	CASE WHEN EXISTS (
		SELECT idx FROM courses_team
		WHERE course_id = #{courseId} AND idx = #{teamId}
	)  THEN true ELSE false END
