mapper.insert:

	select nextval('members_idx_seq'::regclass)

	INSERT INTO members(
		idx, team_status, team_id, user_id
	) VALUES (
		#{idx}, #{teamStatus}, #{teamId}, #{userId}
	)

mapper.update:
UPDATE members
	
		 member_name = #{memberName}, 
		 member_desc = #{memberDesc}, 
		 member_create_id = #{memberCreateId} 
	
WHERE idx = #{idx}
