mapper.insert:

   select nextval('modules_idx_seq'::regclass)

   INSERT INTO modules(
      idx, module_name, module_create_date, module_modify_date, module_metadata, module_data
   ) VALUES (
      #{idx}, #{moduleName}, #{moduleCreateDate}, #{moduleModifyDate}, #{moduleMetadata}, #{moduleData}
   )

mapper.update:
UPDATE modules
module_name = #{moduleName}, module_create_date = #{moduleCreateDate}, module_modify_date = #{moduleModifyDate}, module_metadata = #{moduleMetadata}, module_data = #{moduleData}
WHERE idx = #{idx}
