mapper.update:
UPDATE pngo_user

	 username = #{username}, 
	 passwd = #{passwd}, 
	 firstName = #{firstName}

WHERE idx = #{idx}

mapper.select:
SELECT 
	CASE WHEN EXISTS (
		SELECT idx FROM pngo_user
		WHERE username = #{username}
	)  THEN true ELSE false END
