mapper.select:
SELECT idx, school_id, school_name, school_level, school_status, school_edu_office_name, school_edu_office_code, school_sido_office_name, school_sido_office_code, school_addr, school_build_date, school_establish_type, school_lat, school_lon, school_branch_type, school_addr_road, school_reference_date, school_data_create_date, school_date_edit_date FROM schools
school_name = #{keyword}
school_edu_office_name = #{keyword}
OFFSET ${offset} LIMIT ${limit}

mapper.update:
UPDATE schools
school_id = #{schoolId}, school_name = #{schoolName}, school_level = #{schoolLevel}, school_status = #{schoolStatus}, school_edu_office_name = #{schoolEduOfficeName}, school_edu_office_code = #{schoolEduOfficeCode}, school_sido_office_name = #{schoolSidoOfficeName}, school_sido_office_code = #{schoolSidoOfficeCode}, school_addr = #{schoolAddr}, school_build_date = #{schoolBuildDate}, school_establish_type = #{schoolEstablishType}, school_lat = #{schoolLat}, school_lon = #{schoolLon}, school_branch_type = #{schoolBranchType}, school_addr_road = #{schoolAddrRoad}, school_reference_date = #{schoolReferenceDate}, school_data_create_date = #{schoolDataCreateDate}, school_date_edit_date = #{schoolDateEditDate}
WHERE idx = #{idx}
