mapper.select:
SELECT idx, userid, password, user_email, user_name, user_division, user_state FROM users
userid = #{userid}
idx = #{idx}

mapper.insert:
INSERT INTO users(
idx, userid, password, user_email, user_name,
user_division
) VALUES (
#{idx}, #{userid}, #{password}, #{userEmail},
#{userName}, #{userDivision}
)

mapper.update:
UPDATE users

	 user_name = #{userName}, 
	 password = #{password}, 
	 user_division = #{userDivision}, 
	 user_state = #{userState} 

WHERE idx = #{idx}

mapper.select:
SELECT 
	CASE WHEN EXISTS (
		SELECT idx FROM users
		WHERE userid = #{userid}
	)  THEN true ELSE false END
